export function plural(v) {
  const n = Math.abs(v);

  if (n % 10 === 1 && n % 100 !== 11) {
    return 0;
  }

  if (n % 10 >= 2 && n % 10 <= 4 && (n % 100 < 10 || n % 100 >= 20)) {
    return 1;
  }

  return 2;
}
export function decimalsCount(number) {
  if (!Number.isFinite(number)) return 0;
  let e = 1;
  let p = 0;

  while (Math.round(number * e) / e !== number) {
    e *= 10;
    p++;
  }

  return p;
}