import React from 'react'; // eslint-disable-next-line import/extensions

import { nanoid } from 'nanoid/non-secure';

const getName = Component => Component && Component.constructor && Component.constructor.name || 'TCSid';

function generateId(Component) {
  const componentName = getName(Component);
  const randomId = nanoid(10);
  return `${componentName}_${randomId}`;
}

function useId(id) {
  const idRef = React.useRef();

  if (id) {
    return id;
  }

  if (!idRef.current) {
    idRef.current = generateId();
  }

  return idRef.current;
}

export default generateId;
export { useId };