import type React from 'react';
import type { InputAttributes } from './types';

declare const inputLayoutHOC: <T extends { inputMode?: InputAttributes['inputMode'] }>(
    WrappedComponent: React.ComponentType<T>,
    params?: { nativeInputMode?: boolean }
) => React.ComponentType<
    Omit<T, 'inputMode'> & {
        /**
         * Выбор раскладки, в которую будут перведены введеные символы
         */
        switchLayout?: 'toLatin' | 'toCyrillic';
        /**
         * Первод введенных символов в верхний регистр
         */
        toUpperCase?: boolean;
        /**
         * Сохранять регистр оригинальной строки
         */
        preserveRegister?: boolean;
        /**
         * Массив ключей payload в onChange, раскладку, которых необходимо переводить
         */
        keysForModify?: string[];
    }
>;

export default inputLayoutHOC;
