const getNumberValue = value => {
  if (typeof value === 'string') {
    const withoutSpaceValue = value.replace(/\s/g, '');
    const numberValue = parseFloat(withoutSpaceValue);
    return !Number.isNaN(Number(numberValue)) ? numberValue : undefined;
  }

  return typeof value === 'number' ? value : undefined;
};

export default getNumberValue;