import { spacing, SPACING_ALIAS } from '@platform-ui/constants';
export const getSpacing = value => {
  if (!value) return;

  if (typeof value === 'number') {
    return `${value}px`;
  }

  return value.split(' ').reduce((accum, valueItem) => {
    const spacingItem = valueItem in SPACING_ALIAS ? spacing[SPACING_ALIAS[valueItem]] : valueItem;
    return `${accum} ${spacingItem}`;
  }, '').trim();
};