export function menuItemToContextMenu({
  active,
  path,
  title,
  onClick
}, clickHandler) {
  const handleClick = onClick || clickHandler;
  return {
    active,
    attributes: {
      href: path
    },
    tagName: path ? 'a' : 'span',
    title,
    onClick: e => {
      if (handleClick) {
        handleClick(e, {
          path
        });
      }
    }
  };
}