"use strict";

var columnsCount = 12;

module.exports = function (_, name) {
  var resolutionName = !name ? '' : name + "_";
  var styles = {};

  for (var i = 1; i <= columnsCount; i++) {
    var width = Number(i) / columnsCount * 100;
    var columnWidth = width.toFixed(width % 1 && 6);
    styles["&_size_" + resolutionName + i] = {
      flex: "0 0 " + columnWidth + "%",
      maxWidth: columnWidth + "%"
    };
    styles["&_offset_" + resolutionName + i] = {
      marginLeft: columnWidth + "%"
    };
  }

  return styles;
};