/**
 * Возвращает новое значение мультиселекта на основе текущего и новой выбранной опции
 */
const updateMultiselectValue = (value, optionValue) => {
  const index = value.indexOf(optionValue);

  if (index === -1) {
    return [...value, optionValue];
  }

  const result = [...value];
  result.splice(index, 1);
  return result;
};

const getOptionsLength = options => options && options.reduce((result, option) => result + (option.options ? getOptionsLength(option.options) : 1), 0);

export { updateMultiselectValue, getOptionsLength };