import { useState, useCallback } from 'react';
export const useCheckedState = props => {
  const {
    checked,
    defaultChecked,
    onChange
  } = props;
  const [stateChecked, setStateChecked] = useState(defaultChecked || false);
  return {
    checked: checked !== undefined ? checked : stateChecked,
    onChange: useCallback((event, params) => {
      setStateChecked(params.checked);
      onChange && onChange(event, params);
    }, [onChange])
  };
};