import { useState, useCallback } from 'react';
export const useValueState = props => {
  const {
    emptyValue,
    defaultValue = emptyValue,
    value,
    onChange
  } = props;
  const [stateValue, setStateValue] = useState(defaultValue);
  return {
    value: value !== undefined ? value : stateValue,
    onChange: useCallback((event, params) => {
      setStateValue(params.value);
      onChange && onChange(event, params);
    }, [onChange])
  };
};