export const getOnClickHandler = (handlers, activeIndex, title) => e => handlers.forEach(handler => handler && handler(e, {
  activeIndex,
  title
}));
export const getContextMenuItems = ({
  activeIndex,
  onItemClick,
  items,
  disabled
}) => items.map((item, index) => ({
  additionalContent: item.additionalContent,
  disabled: disabled || item.disabled,
  title: item.title,
  onClick: getOnClickHandler([onItemClick, item.onClick], index, item.title),
  active: index === activeIndex
}));
export const getKey = (item, index) => item.key || item.title || (typeof item.additionalContent === 'string' ? item.additionalContent : undefined) || index;